package com.rtmap.airport.group.util;import com.rtmap.airport.group.result.CommonResponse;import org.springframework.validation.BindingResult;/** * @Auther: shan * @Date:2022/2/19 * @Description: 传参校验统一消息体 */public class BindingResultUtil {    public static CommonResponse validate(BindingResult bindingResult) {        if (bindingResult.hasErrors()) {            String message = bindingResult.getFieldError().getDefaultMessage();            return new CommonResponse("-1", message);        } else {            return null;        }    }}